                ViewState["totIGST"] = (!String.IsNullOrEmpty(totIGST.ToString())) ? Convert.ToDecimal(totIGST) : 0;
                // ------------------------------------------------
                if (hdnSerialKey.Value == "SA98-6HY9-HU67-LORF")        // ShaktiPet    
                {
                    TextBox txt1 = (TextBox)e.Item.FindControl("edQuantity");
                    txt1.Enabled = false;
